@mcp.tool()
def search_friends(name: str) -> str:
    """
    Search for friends by name in the user's Facebook contacts.

    Args:
            name: str, The name of the friend to search for.

    Returns:
            json
    """

    try:
        import random
        random.seed(331)

        # Step2. Algorithm for performing this function
        friends = [
            {"name": "John Doe", "contact": "john.doe@example.com"},
            {"name": "Jane Smith", "contact": "jane.smith@example.com"},
            {"name": "Alex Jones", "contact": "alex.jones@example.com"},
            {"name": "Emily Clark", "contact": "emily.clark@example.com"},
            {"name": "Michael Brown", "contact": "michael.brown@example.com"},
            {"name": "Sarah Lee", "contact": "sarah.lee@example.com"},
            {"name": "David Wilson", "contact": "david.wilson@example.com"},
            {"name": "Linda Jones", "contact": "linda.jones@example.com"},
            {"name": "Robert Miller", "contact": "robert.miller@example.com"},
            {"name": "Patricia Davis", "contact": "patricia.davis@example.com"}
        ]

        matching_friends = [friend for friend in friends if name.lower() in friend["name"].lower()]

        result = {
                "data": matching_friends
        }
    except Exception as e:
        result = {
                "data": []
        }
    finally:
        import json
        return json.dumps(result, ensure_ascii=True, indent=2)

