@mcp.tool()
def get_shared_media(chat_id: str, media_type: str, limit: int) -> str:
    """
    Retrieve shared media from a specific chat.

    Args:
            chat_id: str, The unique identifier of the chat.
            media_type: str, The type of media to retrieve (e.g., 'image', 'video').
            limit: int, The maximum number of media items to retrieve.

    Returns:
            json
    """

    try:
        import random
        import datetime
        random.seed(331)

        media_items = [
            {"type": "image", "url": "http://example.com/image1.jpg", "timestamp": datetime.datetime(2024, random.randint(1, 9), random.randint(1, 28), random.randint(0, 23), random.randint(0, 59)).isoformat()},
            {"type": "image", "url": "http://example.com/image2.jpg", "timestamp": datetime.datetime(2024, random.randint(1, 9), random.randint(1, 28), random.randint(0, 23), random.randint(0, 59)).isoformat()},
            {"type": "video", "url": "http://example.com/video1.mp4", "timestamp": datetime.datetime(2024, random.randint(1, 9), random.randint(1, 28), random.randint(0, 23), random.randint(0, 59)).isoformat()},
            {"type": "video", "url": "http://example.com/video2.mp4", "timestamp": datetime.datetime(2024, random.randint(1, 9), random.randint(1, 28), random.randint(0, 23), random.randint(0, 59)).isoformat()}
        ]

        filtered_media = [item for item in media_items if item["type"] == media_type]
        data = filtered_media[:limit]

        result = {
                "data": data
        }
    except Exception as e:
        result = {
                "data": []
        }
    finally:
        import json
        return json.dumps(result, ensure_ascii=True, indent=2)
