@mcp.tool()
def get_upcoming_events(start_date: str, end_date: str) -> str:
    """
    Retrieves a list of upcoming events within a specified date range from the Samsung Calendar.

    Args:
            start_date: str, The start date for the range in 'YYYY-MM-DD' format.
            end_date: str, The end date for the range in 'YYYY-MM-DD' format.

    Returns:
            json
    """

    try:
        import random
        from datetime import datetime, timedelta
        random.seed(331)

        # Step2. Algorithm for performing this function
        def random_date(start, end):
            return start + timedelta(days=random.randint(0, (end - start).days))

        start = datetime.strptime(start_date, "%Y-%m-%d")
        end = datetime.strptime(end_date, "%Y-%m-%d")

        events = []
        for _ in range(random.randint(3, 7)):
            event_date = random_date(start, end)
            events.append({
                "title": random.choice([
                    "Team Meeting",
                    "Doctor's Appointment",
                    "Lunch with Sarah",
                    "Project Deadline",
                    "Yoga Class",
                    "Birthday Party"
                ]),
                "location": random.choice([
                    "Conference Room A",
                    "Downtown Clinic",
                    "Sarah's Cafe",
                    "Office",
                    "Community Center",
                    "John's House"
                ]),
                "timestamp": event_date.isoformat(),
                "dataType": "text"
            })

        result = {
                "data": events
        }
    except Exception as e:
        result = {
                "data": []
        }
    finally:
        import json
        return json.dumps(result, ensure_ascii=True, indent=2)

@mcp.tool()
def add_event(title: str, date: str, location: str) -> str:
    """
    Adds a new event to the Samsung Calendar.

    Args:
            title: str, The title of the event.
            date: str, The date of the event in 'YYYY-MM-DD' format.
            location: str, The location of the event.

    Returns:
            json
    """

    try:
        import random
        from datetime import datetime
        random.seed(331)

        # Step2. Algorithm for performing this function
        event_date = datetime.strptime(date, "%Y-%m-%d")
        event = {
            "title": title,
            "location": location,
            "timestamp": event_date.isoformat(),
            "dataType": "text"
        }

        result = {
                "data": [event]
        }
    except Exception as e:
        result = {
                "data": []
        }
    finally:
        import json
        return json.dumps(result, ensure_ascii=True, indent=2)

