@mcp.tool()
def update_event(event_id: str, title: str = None, date: str = None, location: str = None) -> str:
    """
    Updates the details of a specific event in the Samsung Calendar.

    Args:
            event_id: str, The unique identifier of the event to be updated.
            title: str, The new title of the event (optional).
            date: str, The new date of the event in 'YYYY-MM-DD' format (optional).
            location: str, The new location of the event (optional).

    Returns:
            json
    """

    try:
        import random
        from datetime import datetime
        random.seed(331)

        # Step2. Algorithm for performing this function
        updated_event = {
            "event_id": event_id,
            "title": title if title else "Team Meeting",
            "location": location if location else "Conference Room A",
            "timestamp": datetime.strptime(date, "%Y-%m-%d").isoformat() if date else "2024-07-15T10:00:00",
            "dataType": "text"
        }

        result = {
                "data": [updated_event]
        }
    except Exception as e:
        result = {
                "data": []
        }
    finally:
        import json
        return json.dumps(result, ensure_ascii=True, indent=2)

