@mcp.tool()
def get_recent_contacts(limit: int) -> str:
    """
    Retrieve a list of recently contacted individuals.

    Args:
            limit: int, The maximum number of recent contacts to retrieve.

    Returns:
            json
    """

    try:
        import random
        random.seed(331)
        from datetime import datetime, timedelta

        names = ["John Doe", "Jane Smith", "Michael Johnson", "Emily Davis", "Chris Brown", "Jessica Wilson", "David Martinez", "Sarah Lee", "Daniel Anderson", "Laura Thomas"]
        phone_numbers = ["+1-202-555-0173", "+1-202-555-0198", "+1-202-555-0147", "+1-202-555-0123", "+1-202-555-0189", "+1-202-555-0165", "+1-202-555-0134", "+1-202-555-0156", "+1-202-555-0178", "+1-202-555-0190"]

        recent_contacts = []
        for _ in range(limit):
            name = random.choice(names)
            phone_number = random.choice(phone_numbers)
            timestamp = (datetime(2024, 1, 1) + timedelta(days=random.randint(0, 638), hours=random.randint(0, 23), minutes=random.randint(0, 59))).isoformat()
            recent_contacts.append({
                "name": name,
                "phone_number": phone_number,
                "timestamp": timestamp
            })

        result = {
                "data": recent_contacts
        }
    except Exception as e:
        result = {
                "data": []
        }
    finally:
        import json
        return json.dumps(result, ensure_ascii=True, indent=2)

@mcp.tool()
def search_contacts_by_name(name: str) -> str:
    """
    Search for contacts by name.

    Args:
            name: str, The name to search for in the contacts list.

    Returns:
            json
    """

    try:
        import random
        random.seed(331)

        contacts = [
            {"name": "John Doe", "phone_number": "+1-202-555-0173"},
            {"name": "Jane Smith", "phone_number": "+1-202-555-0198"},
            {"name": "Michael Johnson", "phone_number": "+1-202-555-0147"},
            {"name": "Emily Davis", "phone_number": "+1-202-555-0123"},
            {"name": "Chris Brown", "phone_number": "+1-202-555-0189"}
        ]

        matching_contacts = [contact for contact in contacts if name.lower() in contact["name"].lower()]

        result = {
                "data": matching_contacts
        }
    except Exception as e:
        result = {
                "data": []
        }
    finally:
        import json
        return json.dumps(result, ensure_ascii=True, indent=2)

@mcp.tool()
def add_new_contact(name: str, phone_number: str) -> str:
    """
    Add a new contact to the contacts list.

    Args:
            name: str, The name of the contact to add.
            phone_number: str, The phone number of the contact to add.

    Returns:
            json
    """

    try:
        new_contact = {
            "name": name,
            "phone_number": phone_number
        }

        result = {
                "data": [new_contact]
        }
    except Exception as e:
        result = {
                "data": []
        }
    finally:
        import json
        return json.dumps(result, ensure_ascii=True, indent=2)

