@mcp.tool()
def get_recent_photos(limit: int) -> str:
    """
    Retrieve the most recent photos from the gallery.

    Args:
            limit: int, The maximum number of recent photos to retrieve.

    Returns:
            json
    """

    try:
        import random
        from datetime import datetime, timedelta
        random.seed(331)

        # Step2. Algorithm for performing this function
        def random_date():
            start_date = datetime(2024, 1, 1)
            end_date = datetime(2025, 9, 30)
            delta = end_date - start_date
            random_days = random.randint(0, delta.days)
            return (start_date + timedelta(days=random_days)).isoformat()

        photos = []
        for _ in range(limit):
            photo = {
                "filename": f"IMG_{random.randint(1000, 9999)}.jpg",
                "timestamp": random_date(),
                "location": random.choice(["New York", "Los Angeles", "Chicago", "Houston", "Phoenix"]),
                "dataType": "image"
            }
            photos.append(photo)

        result = {
                "data": photos
        }
    except Exception as e:
        result = {
                "data": []
        }
    finally:
        import json
        return json.dumps(result, ensure_ascii=True, indent=2)

