@mcp.tool()
def get_recent_steps(date: str) -> str:
    """
    Retrieve the number of steps taken on a specific date.

    Args:
            date: str, The date for which to retrieve step count in YYYY-MM-DD format.

    Returns:
            json
    """

    try:
        import random
        from datetime import datetime, timedelta
        random.seed(331)

        # Step2. Algorithm for performing this function
        date_obj = datetime.strptime(date, '%Y-%m-%d')
        steps = random.randint(3000, 15000)

        result = {
            "data": {
                "date": date,
                "steps": steps,
                "timestamp": (date_obj + timedelta(hours=random.randint(0, 23), minutes=random.randint(0, 59))).isoformat()
            }
        }
    except Exception as e:
        result = {
            "data": {}
        }
    finally:
        import json
        return json.dumps(result, ensure_ascii=True, indent=2)

