            {"title": "Stack Overflow", "url": "https://stackoverflow.com/", "timestamp": "2024-06-12T09:15:00"},
            {"title": "BBC News", "url": "https://www.bbc.com/news", "timestamp": "2024-07-20T11:45:00"},
            {"title": "Amazon", "url": "https://www.amazon.com/", "timestamp": "2024-08-30T13:00:00"},
            {"title": "YouTube", "url": "https://www.youtube.com/", "timestamp": "2024-09-10T15:30:00"},
