@mcp.tool()
def get_reminders_by_date(date: str) -> str:
    """
    Retrieve reminders scheduled for a specific date.

    Args:
            date: str, The date to retrieve reminders for in YYYY-MM-DD format.

    Returns:
            json
    """

    try:
        import random
