@mcp.tool()
def get_current_weather(location: str) -> str:
    """
    Retrieves the current weather information for a specified location.

    Args:
            location: str, The name of the location for which to retrieve the weather.

    Returns:
            json
    """

    try:
        import random
        random.seed(331)

        weather_conditions = ["Sunny", "Cloudy", "Rainy", "Snowy", "Windy", "Stormy"]
        temperatures = [random.randint(-10, 40) for _ in range(6)]
        humidity_levels = [random.randint(20, 100) for _ in range(6)]

        result = {
            "data": {
                "location": location,
                "timestamp": "2024-07-15T14:23:00",
                "weather": random.choice(weather_conditions),
                "temperature": random.choice(temperatures),
                "humidity": random.choice(humidity_levels)
            }
        }
    except Exception as e:
        result = {
            "data": {}
        }
    finally:
        import json
        return json.dumps(result, ensure_ascii=True, indent=2)
@mcp.tool()
def get_weekly_forecast(location: str) -> str:
    """
    Provides a 7-day weather forecast for a specified location.

    Args:
            location: str, The name of the location for which to retrieve the forecast.

    Returns:
            json
    """

    try:
        import random
        random.seed(331)

        weather_conditions = ["Sunny", "Cloudy", "Rainy", "Snowy", "Windy", "Stormy"]
        temperatures = [random.randint(-10, 40) for _ in range(7)]
        humidity_levels = [random.randint(20, 100) for _ in range(7)]

        forecast = []
        for i in range(7):
            day_forecast = {
                "day": f"Day {i+1}",
                "weather": random.choice(weather_conditions),
                "temperature": temperatures[i],
                "humidity": humidity_levels[i]
            }
            forecast.append(day_forecast)

        result = {
            "data": {
                "location": location,
                "timestamp": "2024-08-01T09:00:00",
                "forecast": forecast
            }
        }
    except Exception as e:
        result = {
            "data": {}
        }
    finally:
        import json
        return json.dumps(result, ensure_ascii=True, indent=2)
