@mcp.tool()
def find_contact_by_name(name: str) -> str:
    """
    Find a contact in Slack by their name.

    Args:
            name: str, The name of the contact to find.

    Returns:
            json
    """

    try:
        import random
        random.seed(331)

        contacts = [
            {"name": "John Doe", "email": "john.doe@example.com", "phone": "555-1234"},
            {"name": "Jane Smith", "email": "jane.smith@example.com", "phone": "555-5678"},
            {"name": "Alex Jones", "email": "alex.jones@example.com", "phone": "555-8765"},
            {"name": "Emily Clark", "email": "emily.clark@example.com", "phone": "555-4321"},
            {"name": "Michael Brown", "email": "michael.brown@example.com", "phone": "555-6789"}
        ]

        filtered_contacts = [contact for contact in contacts if name.lower() in contact["name"].lower()]

        result = {
                "data": filtered_contacts
        }
    except Exception as e:
        result = {
                "data": []
        }
    finally:
        import json
        return json.dumps(result, ensure_ascii=True, indent=2)

@mcp.tool()
def get_channel_files(channel_id: str, limit: int) -> str:
    """
    Retrieve a list of files shared in a specific Slack channel.

    Args:
            channel_id: str, The unique identifier of the Slack channel.
            limit: int, The maximum number of files to retrieve.

    Returns:
            json
    """

    try:
        import random
        from datetime import datetime, timedelta
        random.seed(331)

        file_names = ["project_plan.pdf", "meeting_notes.docx", "budget.xlsx", "presentation.pptx", "design_mockup.png"]
        file_types = ["pdf", "docx", "xlsx", "pptx", "png"]

        data = []
        for _ in range(limit):
            file = {
                "file_name": random.choice(file_names),
                "file_type": random.choice(file_types),
                "timestamp": (datetime(2024, 1, 1) + timedelta(days=random.randint(0, 638), hours=random.randint(0, 23), minutes=random.randint(0, 59))).isoformat()
            }
            data.append(file)

        result = {
                "data": data
        }
    except Exception as e:
        result = {
                "data": []
        }
    finally:
        import json
        return json.dumps(result, ensure_ascii=True, indent=2)

